import React, { Component } from 'react';
import _ from 'lodash';
import ImageList from '../../components/ImageList';
import ImageBox from '../../components/ImageBox';
import InfiniteScroll from "react-infinite-scroll-component";
import prepareImages from "../../utils";
class SearchResults extends Component {

	state = {
		images: [],
		offset: 0
	};

	componentDidMount() {
		this.fetchGifs();
	}

	componentDidUpdate(prevProps) {
		if(this.props.text !== prevProps.text) // Check if it's a new user, you can also use some unique property, like the ID  (this.props.user.id !== prevProps.user.id)
		{
			_.debounce(this.fetchGifs, 1200, {leading: true})();
			this.setState({offset: 0, images: []});
		}
	} 

	fetchGifs = () => {
		const {text} = this.props;
		const apiUrl =
			`https://app.zenserp.com/api/v2/search?q=${text}&hl=en&gl=US&location=United%20States&search_engine=google.com&tbm=isch&num=20&apikey=2b20eb80-aad7-11e9-a80b-f5d42510bbba`
			fetch(apiUrl)
			.then(res => res.json())
			.then(
				(result) => {
					this.setState({
						images: result.image_results.map(prepareImages)
					});
				},
				(error) => {
					console.log(error);
				}
			)
	}

	render() {
		return <InfiniteScroll
          dataLength={this.state.images.length}
          loader={this.props.text ? <h4>Loading...</h4>: <h4>please enter text to search</h4>}
        >
          <ImageList images={this.state.images} renderImage={ImageBox} />
        </InfiniteScroll>
		 ;
	}
}

export default SearchResults;
